('medge', 'medGeATCD', 'Formulaire latéral écran patient principal (atcd)', 'formulaire en colonne latéral du dossier patient (atcd)', 'data_types', 'medical', 'post', NULL, @catID, 'public', 'structure:\r\n  row1:                              \r\n    col1:                            \r\n      size: col-12 col-sm-4 col-lg-4\r\n      bloc:                          \r\n        - poids,plusg={<i class=\"fa fa-clone duplicate\"></i>},class=text-right,plus={kg} 		#34   Poids\n    col2:                            \r\n      size: col-12 col-sm-4 col-lg-4\r\n      bloc:                          \r\n        - taillePatient,plusg={<i class=\"fa fa-clone duplicate\"></i>},class=text-right,plus={cm} 		#35   Taille\n    col3:                              \r\n      size: col-12 col-sm-4 col-lg-4\r\n      bloc:                          \r\n        - imc,readonly,class=text-right,plus={kg/m²} 		#43   IMC\n  row2:\r\n    col1:\r\n      size: col-4\r\n      bloc: \r\n         - taSystolique,plus={mmHg},class=text-right 		#943  TAS\n    col2:\r\n      size: col-4\r\n      bloc: \r\n         - taDiastolique,plus={mmHg},class=text-right 		#944  TAD\n    col3:\r\n      size: col-4\r\n      bloc: \r\n         - freqCardiaque,plus={bpm},class=text-right 		#945  FC\n\r\n  row3:                             \r\n    col1:                           \r\n      size: col-5\r\n      bloc:                          \r\n        - clairanceCreatinine,plus={mL/min},class=text-right 		#922  Clairance créatinine\n    col2:                           \r\n      size: col-7\r\n      bloc:   \r\n        - insuffisanceHepatique                    		#923  Insuffisance hépatique\n\r\n  row4:                             \r\n    col1:                           \r\n      size: 12\r\n      bloc:                          \r\n        - job,rows=1,rows=1                                      		#19   Activité professionnelle\n        - allergies,rows=1                         		#66   Allergies\n        - toxiques,rows=1,rows=1                                 		#42   Toxiques\n  row5:                             \r\n    col1:                             \r\n      size: 12\r\n      bloc:                          \r\n        - medGeAtcdPersoMedicaux,rows=2            		#947  Antécédents médicaux\n        - medGeAtcdPersoChirugicaux,rows=2         		#948  Antécédents chirugicaux\n        - medGeAtcdFamiliaux,rows=2                		#949  Antécédents familiaux', NULL, NULL, NULL, '$(document).ready(function() {\r\n\r\n  //calcul IMC\r\n  if ($(\'#id_imc_id\').length > 0) {\r\n\r\n    imc = imcCalc($(\'#id_poids_id\').val(), $(\'#id_taillePatient_id\').val());\r\n    if (imc > 0) {\r\n      $(\'#id_imc_id\').val(imc);\r\n    }\r\n\r\n    $(\"#patientLatCol\").on(\"keyup\", \"#id_poids_id , #id_taillePatient_id\", function() {\r\n      poids = $(\'#id_poids_id\').val();\r\n      taille = $(\'#id_taillePatient_id\').val();\r\n      imc = imcCalc(poids, taille);\r\n      $(\'#id_imc_id\').val(imc);\r\n      patientID = $(\'#identitePatient\').attr(\"data-patientID\");\r\n      setPeopleDataByTypeName(imc, patientID, \'imc\', \'#id_imc_id\', \'0\');\r\n\r\n    });\r\n  }\r\n\r\n  //ajutement auto des textarea en hauteur\r\n  autosize($(\'#formName_medGeATCD textarea\')); \r\n  \r\n  ////////////////////////////////////////////////////////////////////////\r\n  ///////// Gestion Allergies\r\n\r\n  // ajout Allergies\r\n  $(\"#patientLatCol\").on(\"click\", \"button.getAllergiesPanel\", function(e) {\r\n    e.preventDefault();\r\n    $(\'#texteRechercheAllergie\').attr(\'data-parentid\', $(this).attr(\'data-parentid\'));\r\n  });\r\n  $(\'#searchAllergie\').on(\'shown.bs.modal\', function() {\r\n    $(\"#texteRechercheAllergie\").val(\'\');\r\n    $(\'#codeAllergietrouves\').html(\'\');\r\n    $(\"#texteRechercheAllergie\").focus();\r\n  })\r\n\r\n  // interogation sur mot clé\r\n  $(\"#texteRechercheAllergie\").typeWatch({\r\n    wait: 1000,\r\n    highlight: false,\r\n    allowSubmit: false,\r\n    captureLength: 3,\r\n    callback: function(value) {\r\n      $.ajax({\r\n        url: urlBase + \'/lap/ajax/allergieSearch/\',\r\n        type: \'post\',\r\n        data: {\r\n          term: value,\r\n          parentid: $(\'#texteRechercheAllergie\').attr(\'data-parentid\')\r\n        },\r\n        dataType: \"html\",\r\n        beforeSend: function() {\r\n          $(\'#codeAllergietrouves\').html(\'<div class=\"col-md-12\">Attente des résultats de la recherche ...</div>\');\r\n        },\r\n        success: function(data) {\r\n          $(\'#codeAllergietrouves\').html(data);\r\n        },\r\n        error: function() {\r\n          alert(\'Problème, rechargez la page !\');\r\n        }\r\n      });\r\n    }\r\n  });\r\n\r\n  // ajouter allergie\r\n  $(\"#searchAllergie\").on(\"click\", \"button.addAllergieToPatient\", function(e) {\r\n    origin = $(this);\r\n    label = $(this).attr(\'data-label\');\r\n    $.ajax({\r\n      url: urlBase + \'/lap/ajax/allergieAdd/\',\r\n      type: \'post\',\r\n      data: {\r\n        patientID: $(\'#identitePatient\').attr(\"data-patientID\"),\r\n        codeAller: $(this).attr(\'data-code\'),\r\n        libelleAller: label,\r\n        parentID: $(this).attr(\'data-parentid\')\r\n      },\r\n      dataType: \"json\",\r\n      success: function(data) {\r\n        if (data[\'statut\'] == \'ok\') {\r\n          origin.closest(\'tr\').addClass(\"success\");\r\n          origin.attr(\'disabled\', \'disabled\');\r\n          $(\"#atcdTableauAllergieStruc\").removeClass(\'d-none\');\r\n          $(\"#atcdTableauAllergieStruc tbody\").append(\'<tr class=\"tr{{ id }} table-warning small\"><td>\' + label + \'</td></tr>\');\r\n        } else {\r\n          alert(\'Problème, rechargez la page !\');\r\n        }\r\n      },\r\n      error: function() {\r\n        alert(\'Problème, rechargez la page !\');\r\n      }\r\n    });\r\n  });\r\n\r\n  // retirer allergie\r\n  $(\"#patientLatCol\").on(\"click\", \"button.removeAllergie\", f