('medge', 'medGeSyntheseObs', 'Synthèse grossesse en cours', 'formulaire fixe sur les données de la grossesse en cours', 'data_types', 'medical', 'post', '/patient/ajax/saveCsForm/', @catID, 'public', '---\nstructure:\n  row1:\n    col1:\n      size: 2\n      bloc:\n      - DDR\n    col2:\n      size: 2\n      bloc:\n      - ddg,readonly\n    col3:\n      size: 2\n      bloc:\n      - ddgReel\n    col4:\n      size: 2\n      bloc:\n      - termeDuJour,readonly\n    col5:\n      size: 2\n      bloc:\n      - terme9mois,readonly\n  row2:\n    col1:\n      size: 12\n      bloc:\n      - medGeSyntheseGrossesse,rows=2\n...\n', NULL, 'csNouvelleGrossesse', NULL, '$(document).ready(function() {\r\n\r\n  //ajutement auto des textarea en hauteur\r\n  autosize($(\'#formName_medGeSyntheseObs textarea\')); \r\n  \r\n  // Si on change la DDR\r\n  $(\"#before_DDR\").on(\"dp.change\", function(e) {\r\n    calcAndDisplayTdj();\r\n    calcAndDisplayDdgt();\r\n    calcAndDisplayT9m();\r\n    if (typeof(dicomAutoSendPatient) != \"undefined\") {\r\n      if (dicomAutoSendPatient == true) {\r\n        prepareEcho(\'nopopup\');\r\n      }\r\n    }\r\n  });\r\n\r\n\r\n  // Si on change la DDG retenue\r\n  $(\"#before_ddgReel\").on(\"dp.change\", function(e) {\r\n    calcAndDisplayTdj();\r\n    calcAndDisplayT9m();\r\n    if (typeof(dicomAutoSendPatient) != \"undefined\") {\r\n      if (dicomAutoSendPatient == true) {\r\n        prepareEcho(\'nopopup\');\r\n      }\r\n    }\r\n  });\r\n\r\n  //close grossesseEnCours\r\n  $(\'body\').on(\"click\", \"#closeGro\", function(e) {\r\n    if (confirm(\"Voulez-vous fermer définitivement ce suivi de grossesse ?\")) {\r\n      if (confirm(\"Confirmez-vous réellement ?\")) {\r\n\r\n      } else {\r\n        e.preventDefault();\r\n      }\r\n    } else {\r\n      e.preventDefault();\r\n    }\r\n  });\r\n\r\n  calcAndDisplayDdgt();\r\n  calcAndDisplayTdj();\r\n  calcAndDisplayT9m()\r\n});\r\n\r\n// calculer et afficher terme du jour\r\nfunction calcAndDisplayTdj() {\r\n  tdj = tdjCalc($(\'#id_DDR_id\').val(), $(\'#id_ddgReel_id\').val());\r\n  if (tdj[\'status\'] == \'ok\') {\r\n    $(\'#id_termeDuJour_id\').val(tdj[\'human\']);\r\n    $(\'#id_termeDuJour_id\').attr(\'data-tdj4math\', tdj[\'math\']);\r\n  } else {\r\n    $(\'#id_termeDuJour_id\').val(\'\');\r\n    $(\'#id_termeDuJour_id\').attr(\'data-tdj4math\', \'\');\r\n  }\r\n}\r\n\r\n// calculer et afficher terme 9 mois\r\nfunction calcAndDisplayT9m() {\r\n  t9 = terme9mCalc($(\'#id_DDR_id\').val(), $(\'#id_ddgReel_id\').val());\r\n  if (t9[\'status\'] == \'ok\') {\r\n    $(\'#id_terme9mois_id\').val(t9[\'human\']);\r\n  } else {\r\n    $(\'#id_terme9mois_id\').val(\'\');\r\n  }\r\n}\r\n\r\n\r\n//calculer et afficher DDG théo\r\nfunction calcAndDisplayDdgt() {\r\n  ddgt = ddgtCalc($(\'#id_DDR_id\').val());\r\n  $(\'#id_ddg_id\').val(ddgt);\r\n}'),
('medge', 'medGeSynthesePatient', 'Synthèse patiente', 'formulaire fixe de synthèse', 'data_types', 'medical', 'post', NULL, @catID, 'public', '---\nstructure:\n  row1:\n    col1:\n      size: col-12\n      bloc:\n      - medGeSynthesePatient,rows=2\n...\n', NULL, NULL, NULL, '$(document).ready(function() {\r\n\r\n  //bouton de nouvelle grossesse\r\n  $(\"a.newGro\").on(\"click\", function(e) {\r\n    if (!confirm(\'Voulez-vous installer un nouveau suivi de grossesse ?\')) {\r\n      e.preventDefault();\r\n    }\r\n  });\r\n  \r\n  //ajutement auto des textarea en hauteur\r\n  autosize($(\'#formName_medGeSynthesePatient textarea\')); \r\n \r\n});');
